#include <stdio.h>
int main(void) {
    int n, i;
    int arr[100];
    long long sum = 0;
    printf("Enter count: ");
    if (scanf("%d", &n) != 1 || n < 1 || n > 100) {
        printf("Invalid count\n");
        return 1;
    }
    for (i = 0; i < n; i++) {
        printf("arr[%d] = ", i);
        if (scanf("%d", &arr[i]) != 1) return 1;
        sum += arr[i];
    }
    double avg = (double)sum / n;
    printf("Average = %.2f\n", avg);
    return 0;
}
